<?php

/* @autogenerated */
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Value\Validation\Rule\Min;
use Civi\Lughauth\Shared\Value\Validation\RuleFail;

final class MinUnitTest extends TestCase
{
    public function testValidMinValues(): void
    {
        $rule = new Min(5);
        $this->assertNull($rule->check(5));
        $this->assertNull($rule->check(10));
        $this->assertNull($rule->check('6')); // string convertible
    }

    public function testInvalidMinValues(): void
    {
        $rule = new Min(5);
        $invalid = [4, 0, -1, '3', 4.99];

        foreach ($invalid as $value) {
            $result = $rule->check($value);
            $this->assertInstanceOf(RuleFail::class, $result);
            $this->assertEquals('rule_min', $result->code);
            $this->assertEquals($value, $result->value);
            $this->assertEquals(['min' => 5], $result->expectedValues);
        }
    }
}
