<?php

/* @autogenerated */
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Value\Validation\Rule\NotEmpty;
use Civi\Lughauth\Shared\Value\Validation\RuleFail;

final class NotEmptyUnitTest extends TestCase
{
    public function testValidNonEmptyValues(): void
    {
        $rule = new NotEmpty();
        $this->assertNull($rule->check('hello'));
        $this->assertNull($rule->check([1]));
        $this->assertNull($rule->check(42));
        $this->assertNull($rule->check(true));
    }

    public function testInvalidEmptyValues(): void
    {
        $rule = new NotEmpty();
        $invalid = ['', [], 0, '0', false, null];

        foreach ($invalid as $value) {
            $result = $rule->check($value);
            $this->assertInstanceOf(RuleFail::class, $result);
            $this->assertEquals('rule_not_empty', $result->code);
            $this->assertEquals($value, $result->value);
            $this->assertEquals([], $result->expectedValues);
        }
    }
}
