<?php

/* @autogenerated */
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Value\Validation\Rule\Numeric;
use Civi\Lughauth\Shared\Value\Validation\RuleFail;

final class NumericUnitTest extends TestCase
{
    public function testValidNumericValues(): void
    {
        $rule = new Numeric();
        $this->assertNull($rule->check(123));
        $this->assertNull($rule->check(3.14));
        $this->assertNull($rule->check('42'));
        $this->assertNull($rule->check('3.1416'));
    }

    public function testInvalidNumericValues(): void
    {
        $rule = new Numeric();
        $invalid = ['abc', '', '12a', null, [], true];

        foreach ($invalid as $value) {
            $result = $rule->check($value);
            $this->assertInstanceOf(RuleFail::class, $result);
            $this->assertEquals('rule_numeric', $result->code);
            $this->assertEquals($value, $result->value);
            $this->assertEquals([], $result->expectedValues);
        }
    }
}
