<?php

/* @autogenerated */
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Value\Validation\Rule\PostalCode;
use Civi\Lughauth\Shared\Value\Validation\RuleFail;

final class PostalCodeUnitTest extends TestCase
{
    public function testValidPostalCodes(): void
    {
        $ruleES = new PostalCode('ES');
        $ruleUS = new PostalCode('US');

        $this->assertNull($ruleES->check('28001'));
        $this->assertNull($ruleUS->check('90210'));
    }

    public function testInvalidPostalCodes(): void
    {
        $rule = new PostalCode('ES');
        $invalid = ['ABCDE', '', '123456', '28-001', null];

        foreach ($invalid as $value) {
            $result = $rule->check($value);
            $this->assertInstanceOf(RuleFail::class, $result);
            $this->assertEquals('rule_postal_code', $result->code);
            $this->assertEquals($value, $result->value);
            $this->assertEquals(['countryCode' => 'ES'], $result->expectedValues);
        }
    }
}
