<?php

/* @autogenerated */
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Value\Validation\Rule\Regex;
use Civi\Lughauth\Shared\Value\Validation\RuleFail;

final class RegexUnitTest extends TestCase
{
    public function testValidRegexMatch(): void
    {
        $rule = new Regex('/^[a-z]{3}$/');
        $this->assertNull($rule->check('abc'));
        $this->assertNull($rule->check('xyz'));
    }

    public function testInvalidRegexMatch(): void
    {
        $rule = new Regex('/^[0-9]{4}$/');
        $invalid = ['123', 'abcd', '', null];

        foreach ($invalid as $value) {
            $result = $rule->check($value);
            $this->assertInstanceOf(RuleFail::class, $result);
            $this->assertEquals('rule_regexp', $result->code);
            $this->assertEquals($value, $result->value);
            $this->assertEquals(['pattern' => '/^[0-9]{4}$/'], $result->expectedValues);
        }
    }
}
