<?php

/* @autogenerated */
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Value\Validation\Rule\Regional\Bsn;
use Civi\Lughauth\Shared\Value\Validation\RuleFail;

final class BsnUnitTest extends TestCase
{
    public function testValidBsn(): void
    {
        $rule = new Bsn();
        $this->assertNull($rule->check('111222333'));
    }

    public function testInvalidBsn(): void
    {
        $rule = new Bsn();
        $invalid = ['000000000', '123', 'notabssn', '', null];

        foreach ($invalid as $bsn) {
            $result = $rule->check($bsn);
            $this->assertInstanceOf(RuleFail::class, $result);
            $this->assertEquals('rule_bsn', $result->code);
            $this->assertEquals($bsn, $result->value);
            $this->assertEquals([], $result->expectedValues);
        }
    }
}
