<?php

/* @autogenerated */
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Value\Validation\Rule\Regional\Cnh;
use Civi\Lughauth\Shared\Value\Validation\RuleFail;

final class CnhUnitTest extends TestCase
{
    public function testValidCnh(): void
    {
        $rule = new Cnh();
        $this->assertNull($rule->check('02650306461')); // Ejemplo vÃ¡lido
    }

    public function testInvalidCnh(): void
    {
        $rule = new Cnh();
        $invalid = ['da', 'abcdef', '', null];

        foreach ($invalid as $cnh) {
            $result = $rule->check($cnh);
            $this->assertInstanceOf(RuleFail::class, $result);
            $this->assertEquals('rule_cnh', $result->code);
            $this->assertEquals($cnh, $result->value);
            $this->assertEquals([], $result->expectedValues);
        }
    }
}
