<?php

/* @autogenerated */
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Value\Validation\Rule\Regional\Cnpj;
use Civi\Lughauth\Shared\Value\Validation\RuleFail;

final class CnpjUnitTest extends TestCase
{
    public function testValidCnpj(): void
    {
        $rule = new Cnpj();
        $this->assertNull($rule->check('11.222.333/0001-81')); // vÃ¡lido
    }

    public function testInvalidCnpj(): void
    {
        $rule = new Cnpj();
        $invalid = ['11.222.333/0001-80', '00000000000000', '', null];

        foreach ($invalid as $val) {
            $result = $rule->check($val);
            $this->assertInstanceOf(RuleFail::class, $result);
            $this->assertEquals('rule_cnpj', $result->code);
            $this->assertEquals($val, $result->value);
            $this->assertEquals([], $result->expectedValues);
        }
    }
}
