<?php

/* @autogenerated */
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Value\Validation\Rule\Regional\Cpf;
use Civi\Lughauth\Shared\Value\Validation\RuleFail;

final class CpfUnitTest extends TestCase
{
    public function testValidCpf(): void
    {
        $rule = new Cpf();
        $this->assertNull($rule->check('390.533.447-05')); // vÃ¡lido
    }

    public function testInvalidCpf(): void
    {
        $rule = new Cpf();
        $invalid = ['123.456.789-00', '11111111111'];

        foreach ($invalid as $val) {
            $result = $rule->check($val);
            $this->assertInstanceOf(RuleFail::class, $result);
            $this->assertEquals('rule_cpf', $result->code);
            $this->assertEquals($val, $result->value);
            $this->assertEquals([], $result->expectedValues);
        }
    }
}
