<?php

/* @autogenerated */
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Value\Validation\Rule\Regional\Nif;
use Civi\Lughauth\Shared\Value\Validation\RuleFail;

final class NifUnitTest extends TestCase
{
    public function testValidNif(): void
    {
        $rule = new Nif();
        $this->assertNull($rule->check('12345678Z')); // formato vÃ¡lido
    }

    public function testInvalidNif(): void
    {
        $rule = new Nif();
        $invalid = ['12345678A', 'A2345678Z', '', null];

        foreach ($invalid as $val) {
            $result = $rule->check($val);
            $this->assertInstanceOf(RuleFail::class, $result);
            $this->assertEquals('rule_nif', $result->code);
            $this->assertEquals($val, $result->value);
            $this->assertEquals([], $result->expectedValues);
        }
    }
}
