<?php

/* @autogenerated */
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Value\Validation\Rule\Regional\Nip;
use Civi\Lughauth\Shared\Value\Validation\RuleFail;

final class NipUnitTest extends TestCase
{
    public function testValidNip(): void
    {
        $rule = new Nip();
        $this->assertNull($rule->check('1234563218')); // ejemplo vÃ¡lido
    }

    public function testInvalidNip(): void
    {
        $rule = new Nip();
        $invalid = ['1234563210', 'abcdefghij', '', null];

        foreach ($invalid as $nip) {
            $result = $rule->check($nip);
            $this->assertInstanceOf(RuleFail::class, $result);
            $this->assertEquals('rule_nip', $result->code);
            $this->assertEquals($nip, $result->value);
            $this->assertEquals([], $result->expectedValues);
        }
    }
}
