<?php

/* @autogenerated */
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Value\Validation\Rule\Regional\PortugueseNif;
use Civi\Lughauth\Shared\Value\Validation\RuleFail;

final class PortugueseNifUnitTest extends TestCase
{
    public function testValidPortugueseNif(): void
    {
        $rule = new PortugueseNif();
        $this->assertNull($rule->check('501964843')); // vÃ¡lido
    }

    public function testInvalidPortugueseNif(): void
    {
        $rule = new PortugueseNif();
        $invalid = ['gg123456789', 'abcdefgh'];

        foreach ($invalid as $nif) {
            $result = $rule->check($nif);
            $this->assertInstanceOf(RuleFail::class, $result);
            $this->assertEquals('rule_portuguese_nif', $result->code);
            $this->assertEquals($nif, $result->value);
            $this->assertEquals([], $result->expectedValues);
        }
    }
}
