<?php

/* @autogenerated */
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Value\Validation\Rule\Slug;
use Civi\Lughauth\Shared\Value\Validation\RuleFail;

final class SlugUnitTest extends TestCase
{
    public function testValidSlugs(): void
    {
        $rule = new Slug();
        $this->assertNull($rule->check('valid-slug'));
        $this->assertNull($rule->check('another-slug'));
        $this->assertNull($rule->check('abc'));
    }

    public function testInvalidSlugs(): void
    {
        $rule = new Slug();
        $invalid = ['invalid slug', 'slug!', 'Ã¤bc', '', 'slug@123', 'another_slug'];

        foreach ($invalid as $slug) {
            $result = $rule->check($slug);
            $this->assertInstanceOf(RuleFail::class, $result);
            $this->assertEquals('rule_slug', $result->code);
            $this->assertEquals($slug, $result->value);
            $this->assertEquals([], $result->expectedValues);
        }
    }
}
