<?php

/* @autogenerated */
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Value\Validation\Rule\Uppercase;
use Civi\Lughauth\Shared\Value\Validation\RuleFail;

final class UppercaseUnitTest extends TestCase
{
    public function testValidUppercase(): void
    {
        $rule = new Uppercase();
        $this->assertNull($rule->check(''));
        $this->assertNull($rule->check('ABC'));
        $this->assertNull($rule->check('HELLO'));
        $this->assertNull($rule->check('HELLO123'));
    }

    public function testInvalidUppercase(): void
    {
        $rule = new Uppercase();
        $invalid = ['abc', 'AbC', 'HELLo123', 'dante', 'ÁÉÍo', 'hello world'];

        foreach ($invalid as $val) {
            $result = $rule->check($val);
            $this->assertInstanceOf(RuleFail::class, $result);
            $this->assertEquals('rule_uppercase', $result->code);
            $this->assertEquals($val, $result->value);
            $this->assertEquals([], $result->expectedValues);
        }
    }
}
