<?php

/* @autogenerated */
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Value\Validation\Rule\Url;
use Civi\Lughauth\Shared\Value\Validation\RuleFail;

final class UrlUnitTest extends TestCase
{
    public function testValidUrls(): void
    {
        $rule = new Url();
        $this->assertNull($rule->check('https://example.com'));
        $this->assertNull($rule->check('ftp://local'));
        $this->assertNull($rule->check('http://localhost:8080'));
    }

    public function testInvalidUrls(): void
    {
        $rule = new Url();
        $invalid = ['not-a-url', 'example.com', 'http:/incomplete.com', '', 'ftp//invalid.o'];

        foreach ($invalid as $val) {
            $result = $rule->check($val);
            $this->assertInstanceOf(RuleFail::class, $result);
            $this->assertEquals('rule_url', $result->code);
            $this->assertEquals($val, $result->value);
            $this->assertEquals([], $result->expectedValues);
        }
    }
}
