<?php

/* @autogenerated */
declare(strict_types=1);

use PHPUnit\Framework\TestCase;
use Civi\Lughauth\Shared\Value\Validation\Rule\VideoUrl;
use Civi\Lughauth\Shared\Value\Validation\RuleFail;

final class VideoUrlUnitTest extends TestCase
{
    public function testValidVideoUrls(): void
    {
        $rule = new VideoUrl();
        $this->assertNull($rule->check('https://www.youtube.com/watch?v=dQw4w9WgXcQ'));
        $this->assertNull($rule->check('https://vimeo.com/12345678'));
    }

    public function testInvalidVideoUrls(): void
    {
        $rule = new VideoUrl();
        $invalid = ['https://example.com/video.mp4', 'not-a-url', '', null];

        foreach ($invalid as $url) {
            $result = $rule->check($url);
            $this->assertInstanceOf(RuleFail::class, $result);
            $this->assertEquals('rule_video_url', $result->code);
            $this->assertEquals($url, $result->value);
            $this->assertEquals([], $result->expectedValues);
        }
    }
}
